material.script:
attribute float a_size;

void main()
{
	gl_PointSize = a_size;
	gl_Position = projectionMatrix *
                  modelViewMatrix *
                  vec4(position, 1.0);
}

uniform vec3 u_color;
uniform sampler2D u_texture;

void main() 
{
	gl_FragColor = vec4( u_color, 1.0 );
	gl_FragColor = gl_FragColor * texture2D( u_texture, gl_PointCoord );

}
